// Note: this is a meta module which generates the real google-analytics
// tracking code via isogram for the purpose of customising the isogramatic
// parameters :)

var isogram = require('isogram');

/**
 * Remove ga(xxx); calls from ga tracking code.
 */
function stripGaCalls(trackingCode) {
    return trackingCode.replace(/^ga\(.*\);$/gm, '').trim();
}

module.exports = [
    '// Generated by google-analytics.js',
    stripGaCalls(isogram('cudl')),
    '',
    'module.exports = ga;'
].join('\n');
